DROP DATABASE if EXISTS drive;create DATABASE drive;use drive;GRANT ALL PRIVILEGES ON drive.* TO driver@'localhost' IDENTIFIED BY '123456';CREATE TABLE `t_user` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `pwd` varchar(64) NOT NULL,  `name` varchar(20) NOT NULL,  `registDate` varchar(20) NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;CREATE TABLE `t_admin` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `pwd` varchar(64) NOT NULL,  `name` varchar(20) NOT NULL,  `registDate` varchar(20) NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;CREATE TABLE `t_type` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `type` varchar(20) NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;CREATE TABLE `t_subject` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `subject` varchar(20) NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;CREATE TABLE `t_question` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `types` VARCHAR(20) NOT NULL,  `subjectId` int(10) UNSIGNED NOT NULL,  `content` varchar(500) NOT NULL,  `options` varchar(500) NOT NULL,  `answers` varchar(60) NOT NULL,  `totalNum` int(10) DEFAULT 0,  `correctNum` int(10) DEFAULT 0,  `resolve` VARCHAR(500) NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;CREATE TABLE `t_question_comment` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `uid` int(10) UNSIGNED NOT NULL,  `qid` int(10) UNSIGNED NOT NULL,  `parentId` int(10) UNSIGNED NULL,  `content` varchar(60) NOT NULL,  `commentDate` varchar(20) NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;CREATE TABLE `t_mistake` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `uid` int UNSIGNED  NOT NULL,  `qid` int(10) UNSIGNED NOT NULL,  `createDate` varchar(20) NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;CREATE TABLE `t_collection` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `uid` int(10) UNSIGNED NOT NULL,  `qid` int(10) UNSIGNED NOT NULL,  `createDate` varchar(20) NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;CREATE TABLE `t_user_answer` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `uid` int(10) UNSIGNED NOT NULL,  `qid` int(10) UNSIGNED NOT NULL,  `answers` varchar(20) NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;CREATE TABLE `t_video` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `title` varchar(50) NOT NULL,  `intro` varchar(200) NOT NULL,  `context` varchar(500) NOT NULL,  `src` varchar(50) NOT NULL,  `tid` int(10) UNSIGNED NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;CREATE TABLE `t_image` (  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,  `src` varchar(50) NOT NULL,  PRIMARY KEY (`id`))ENGINE=INNODB CHARSET=utf8;ALTER TABLE `t_question` ADD CONSTRAINT `question_subject` FOREIGN KEY (`subjectId`) REFERENCES `t_subject` (`id`);ALTER TABLE `t_question_comment` ADD CONSTRAINT `comment_question` FOREIGN KEY (`qid`) REFERENCES `t_question` (`id`) ON DELETE CASCADE;ALTER TABLE `t_question_comment` ADD CONSTRAINT `comment_user` FOREIGN KEY (`uid`) REFERENCES `t_user` (`id`) ON DELETE CASCADE;ALTER TABLE `t_question_comment` ADD CONSTRAINT `comment_parent` FOREIGN KEY (`parentId`) REFERENCES `t_question_comment` (`id`) ON DELETE CASCADE;ALTER TABLE `t_mistake` ADD CONSTRAINT `mistake_user` FOREIGN KEY (`uid`) REFERENCES `t_user` (`id`) ON DELETE CASCADE;ALTER TABLE `t_mistake` ADD CONSTRAINT `mistake_question` FOREIGN KEY (`qid`) REFERENCES `t_question` (`id`) ON DELETE CASCADE;ALTER TABLE `t_collection` ADD CONSTRAINT `collection_user` FOREIGN KEY (`uid`) REFERENCES `t_user` (`id`) ON DELETE CASCADE;ALTER TABLE `t_collection` ADD CONSTRAINT `collection_question` FOREIGN KEY (`qid`) REFERENCES `t_question` (`id`) ON DELETE CASCADE;ALTER TABLE `t_user_answer` ADD CONSTRAINT `answer_user` FOREIGN KEY (`uid`) REFERENCES `t_user` (`id`) ON DELETE CASCADE;ALTER TABLE `t_user_answer` ADD CONSTRAINT `answer_question` FOREIGN KEY (`qid`) REFERENCES `t_question` (`id`) ON DELETE CASCADE;ALTER TABLE `t_video` ADD CONSTRAINT `video_type` FOREIGN KEY (`tid`) REFERENCES `t_type` (`id`) ON DELETE CASCADE;INSERT INTO t_type( type) VALUES ('单选题');INSERT INTO t_type (type) VALUES ('判断题');INSERT INTO t_type (type) VALUES ('图片题');INSERT INTO t_type (type) VALUES ('速度题');INSERT INTO t_type (type) VALUES ('文字题');INSERT INTO t_type (type) VALUES ('多选题');INSERT INTO t_subject(`subject`) VALUES ('科目一');INSERT INTO t_subject(`subject`) VALUES ('科目二');INSERT INTO t_subject(`subject`) VALUES ('科目三');INSERT INTO t_subject(`subject`) VALUES ('科目四');