mapper.update:
UPDATE t_user_answer SET
answers = #{answers}
WHERE id = #{id};

mapper.select:
SELECT u.id uid, q.id qid, a.answers answers
FROM t_user_answer a, t_user u, t_question q
WHERE a.uid = #{id}
AND a.uid = u.id
AND a.qid = q.id;

SELECT u.id uid, q.id qid, a.answers answers
FROM t_user_answer a, t_user u, t_question q
WHERE a.qid = #{id}
AND a.uid = u.id
AND a.qid = q.id;
